	destroy: function() {
		
		this.removeFlag();
		this.el.remove();
		
		props = ['el','flagMsgEl'];
		for (i = 0; i < props.length; i++) {
			delete this[props[i]];
		}
		
		// call super.destroy()
		Pulse.FieldMonitor.superclass.destroy.call(this);
		
	}
	
